targets.expr:
(
  histogram_quantile(0.99,(sum by (le, pod) (
    irate(pyroscope_metastore_boltdb_persist_snapshot_duration_seconds_bucket{namespace="$namespace"}[$__rate_interval])))
  ) * on(pod) group_left() (pyroscope_metastore_raft_state{namespace="$namespace", state="Leader"} >= 1) 
)

(
  histogram_quantile(0.99,(sum by (le, pod) (
    irate(pyroscope_metastore_boltdb_persist_snapshot_duration_seconds_bucket{namespace="$namespace"}[$__rate_interval])))
  ) * on(pod) group_left() (pyroscope_metastore_raft_state{namespace="$namespace", state!="Leader"} >= 1) 
)

(
  histogram_quantile(1,(sum by (le, pod) (
    irate(pyroscope_metastore_boltdb_persist_snapshot_duration_seconds_bucket{namespace="$namespace"}[$__rate_interval])))
  ) * on(pod) group_left() (pyroscope_metastore_raft_state{namespace="$namespace", state="Leader"} >= 1) 
)

max(
    kube_pod_container_resource_requests{job=~"(.*/)?kube-state-metrics", namespace="$namespace", resource="cpu"}
  * on(namespace,pod)
    group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~".*-metastore"}
)

max(
    kube_pod_container_resource_limits{job=~"(.*/)?kube-state-metrics", namespace="$namespace", resource="cpu"}
  * on(namespace,pod)
    group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~".*-metastore"}
)

max(
    kube_pod_container_resource_requests{job=~"(.*/)?kube-state-metrics", namespace="$namespace", resource="memory"}
  * on(namespace,pod)
    group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~".*-metastore"}
)

max(
    kube_pod_container_resource_limits{job=~"(.*/)?kube-state-metrics", namespace="$namespace", resource="memory"}
  * on(namespace,pod)
    group_left(workload) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=".*-metastore"}
)

irate(node_disk_io_time_seconds_total{cluster="$cluster"}[$__rate_interval])
+ on(instance, device) group_left(pod)
topk by(instance, device) (
  1,
  0 * label_replace(
    container_blkio_device_usage_total{
      cluster="$cluster",
      namespace="$namespace",
      container="metastore",
      operation="Write",
    },
    "device", "$1", "device", "/dev/(.*)"
  )
)
